import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [authenticated, setAuthenticated] = useState(false); // Set initial state to false

  // Function to handle user logout
  const handleLogout = () => {
    // Perform logout logic
    setAuthenticated(false); // Set authentication state to false
  };

  return (
    <div>
      <ul className='nav'>
        <li><Link to="/">Home</Link></li>
        {!authenticated ? (
          // Show these links if not authenticated
          <>
            <li><Link to="/Login">Login</Link></li>
            <li><Link to="/Register">Register</Link></li>
          </>
        ) : (
          // Show this link if authenticated
          <li><Link to="/" onClick={handleLogout}>Logout</Link></li>
        )}
        {authenticated && (
          // Show this link if authenticated
          <li><Link to="/protected">Employee</Link></li>
        )}
      </ul>
    </div>
  );
};

export default Navbar;
