import React, { useEffect, useState } from 'react';
import axios from 'axios';
const electronicsItems = [
  { id: 1, name: 'Smartphone', price: '$500', image: 'smartphone.jpg' },
  { id: 2, name: 'Laptop', price: '$1000', image: 'laptop.jpg' },
  { id: 3, name: 'Headphones', price: '$100', image: 'headphones.jpg' },
];

const Protected = () => {
  const [message, setMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('userToken');

    if (token) {
     
      const config = {
        headers: {
          Authorization: `${token}`,
        },
      };

      axios.get('http://localhost:5000/protected', config)
        .then(response => {
          setMessage(response.data);
        })
        .catch(error => {
          console.error('Error fetching protected data', error);
        });
    }
  }, []);

  return (
    <div>
      {message ? (
        <div>
       <div>
      <h1>Electronics Items</h1>
      <ul>
        {electronicsItems.map(item => (
          <li key={item.id}>
            <strong>{item.name}</strong> - {item.price}
          </li>
        ))}
      </ul>
    </div>  
        </div>
      ) : (
        <h2>Authentication Required</h2>
      )}
    
      
    </div>
  );
};

export default Protected;
