import React, { useState } from 'react'
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {

const[email,setEmail] = useState("");
const[password,setPassword] = useState("");
const [token, setToken] =  useState("");

const navigate = useNavigate();

 const handlelogin = async (e) => {
    e.preventDefault();
    if (!email || !password) {
        alert('All fields are required.');
        return;
      }
    try {
        const responce = await axios.post("http://localhost:5000/login", {
            email,
            password
        });
        setToken(responce.data.token);
        alert("login successfully");
        navigate("/");
        console.log(responce);
    } catch (error) {
        if (error.response) {
            // Request made and server responded with a status code outside of 2xx
            setError(error.response.data.message);
            console.log(error.response.data);
          } else if (error.request) {
            // The request was made but no response was received
            setError("Request was made but no response was received.");
            console.log(error.request);
          } else {
            // Something happened in setting up the request that triggered an error
            setError("An error occurred while setting up the request.");
            console.log('Error', error.message);
          }
        }
      };
    

  return (
    <div className='form'>
        <h2> Login Form</h2>
        <div className='registration'>
        <label  className='label'>enter your name</label>
        <input type='email' id='email' name='email' placeholder='enter your email'
        onChange={(e) => setEmail(e.target.value) } required/>

        <label  className='label'>enter your password</label>
        <input type='password' id='password' name='password' placeholder='enter your password'
        onChange={(e) => setPassword(e.target.value)} required/>

        <button className='btn' type='button' onClick={handlelogin} >Login</button>
        </div>
    </div>
  )
}

export default Login
